CREATE TABLE army (id BIGINT AUTO_INCREMENT, name VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE attributes (id BIGINT AUTO_INCREMENT, life BIGINT NOT NULL, life_max BIGINT NOT NULL, mana BIGINT NOT NULL, mana_max BIGINT NOT NULL, limit_lvl BIGINT NOT NULL, limit_max BIGINT NOT NULL, strength BIGINT NOT NULL, agility BIGINT NOT NULL, intelligence BIGINT NOT NULL, id_heros BIGINT NOT NULL, INDEX id_heros_idx (id_heros), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE batiment (id BIGINT AUTO_INCREMENT, name VARCHAR(45) NOT NULL, niveau SMALLINT NOT NULL, id_ville BIGINT NOT NULL, theme VARCHAR(45), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX id_ville_idx (id_ville), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE cell (id BIGINT AUTO_INCREMENT, position_x BIGINT NOT NULL, position_y BIGINT NOT NULL, background_image VARCHAR(100), id_type BIGINT, id_map BIGINT NOT NULL, INDEX id_map_idx (id_map), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE cell_type (id BIGINT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, background_image VARCHAR(100), id_type_family BIGINT NOT NULL, INDEX id_type_family_idx (id_type_family), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE cell_type_family (id BIGINT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, background_image VARCHAR(100), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE heros (id BIGINT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, level BIGINT DEFAULT '1' NOT NULL, xp BIGINT DEFAULT '0' NOT NULL, xp_next_level BIGINT NOT NULL, id_user BIGINT NOT NULL, position_x BIGINT DEFAULT '0' NOT NULL, position_y BIGINT DEFAULT '0' NOT NULL, id_map BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX id_map_idx (id_map), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE item (id BIGINT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, history TEXT NOT NULL, description TEXT NOT NULL, thumbnail VARCHAR(45) NOT NULL, real_image VARCHAR(45) NOT NULL, is_original TINYINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE map (id BIGINT AUTO_INCREMENT, name VARCHAR(100) NOT NULL UNIQUE, height BIGINT, width BIGINT, starting_cell_id BIGINT, INDEX starting_cell_id_idx (starting_cell_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE planete (id BIGINT AUTO_INCREMENT, name VARCHAR(45) NOT NULL, principal TINYINT NOT NULL, taille VARCHAR(45) NOT NULL, poids VARCHAR(45) NOT NULL, atmosphere VARCHAR(45) NOT NULL, climat VARCHAR(45) NOT NULL, temperature VARCHAR(45) NOT NULL, id_user BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE position (id BIGINT, position_x BIGINT NOT NULL, position_y BIGINT NOT NULL, id_map BIGINT, PRIMARY KEY(id, id_map)) ENGINE = INNODB;
CREATE TABLE race (id BIGINT, name VARCHAR(45) NOT NULL, theme VARCHAR(45), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE ville (id BIGINT AUTO_INCREMENT, name VARCHAR(45) NOT NULL, capital TINYINT NOT NULL, taille VARCHAR(45) NOT NULL, id_planete BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX id_planete_idx (id_planete), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_profile (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, birthday DATE, title VARCHAR(20), avatar VARCHAR(255), id_race BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), INDEX id_race_idx (id_race), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
ALTER TABLE attributes ADD CONSTRAINT attributes_id_heros_heros_id FOREIGN KEY (id_heros) REFERENCES heros(id);
ALTER TABLE batiment ADD CONSTRAINT batiment_id_ville_ville_id FOREIGN KEY (id_ville) REFERENCES ville(id);
ALTER TABLE cell ADD CONSTRAINT cell_id_map_map_id FOREIGN KEY (id_map) REFERENCES map(id) ON DELETE CASCADE;
ALTER TABLE cell_type ADD CONSTRAINT cell_type_id_type_family_cell_type_family_id FOREIGN KEY (id_type_family) REFERENCES cell_type_family(id);
ALTER TABLE heros ADD CONSTRAINT heros_id_map_map_id FOREIGN KEY (id_map) REFERENCES map(id);
ALTER TABLE map ADD CONSTRAINT map_starting_cell_id_cell_id FOREIGN KEY (starting_cell_id) REFERENCES cell(id) ON DELETE CASCADE;
ALTER TABLE position ADD CONSTRAINT position_id_map_map_id FOREIGN KEY (id_map) REFERENCES map(id);
ALTER TABLE ville ADD CONSTRAINT ville_id_planete_planete_id FOREIGN KEY (id_planete) REFERENCES planete(id);
ALTER TABLE sf_guard_forgot_password ADD CONSTRAINT sf_guard_forgot_password_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_remember_key ADD CONSTRAINT sf_guard_remember_key_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_profile ADD CONSTRAINT sf_guard_user_profile_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_profile ADD CONSTRAINT sf_guard_user_profile_id_race_race_id FOREIGN KEY (id_race) REFERENCES race(id);
